import React, { Fragment, useState } from "react";

import Modal from '../UI/Modal';

import styles from "./RegionsModal.module.css";

const RegionsModal = (props) => {
    const [name, setName] = useState('');

const getRegionData = async () => {
    const response = await fetch('https://myikigai-99b57-default-rtdb.firebaseio.com/');

    const data = await response.json();

    const region = data.filter((region) => region.name === props.name);

    setName(regionName);
}

  const content = (
    <div>{props.name}</div>
  );

  return (
    <Fragment>
      <Modal onClose={props.onClose}>{content}</Modal>
    </Fragment>
  );
};

export default RegionsModal;
