import React, { Fragment, useState, useEffect } from "react";

import Modal from "../UI/Modal";
import HealthModalContent from "../Health/HealthModalContent";

import placeholderImg from "../../images/cherryblossom1.png";
import styles from "./HealthModal.module.css";

const HealthModal = (props) => {
  const [selectedBenefit, setSelectedBenefit] = useState({});
  const [isLoading, setIsLoading] = useState(true);

  const fetchHealthData = async () => {
    const response = await fetch(
      "https://myikigai-99b57-default-rtdb.firebaseio.com/health.json"
    );

    if (!response.ok) {
      console.log("error in fetching health benefits");
    }

    const data = await response.json();

    const transformedHealth = [];

    for (const healthKey in data) {
      transformedHealth.push({
        id: healthKey,
        name: data[healthKey].name,
        flavor: data[healthKey].flavor,
      });
    }

    const selectedRegion = transformedHealth.find(
      (region) => region.name === props.regionName
    );
    setSelectedRegion(selectedRegion);
    setIsLoading(false);
  };

  useEffect(() => {
    fetchRegionData();
  }, [fetchRegionData]);

  const loadingContent = <div className={styles.loading}>Loading...</div>;

  const content = (
    <RegionModalContent
      name={selectedRegion.name}
      img={placeholderImg}
      flavor={selectedRegion.flavor}
    />
  );

  return (
    <Fragment>
      <Modal onClose={props.onClose}>
        {!isLoading && content}
        {isLoading && loadingContent}
      </Modal>
    </Fragment>
  );
};

export default HealthModal;
