import React, { useState } from "react";

import RegionsText from "../components/Regions/RegionsText";
import RegionsMap from "../components/Regions/RegionsMap";
import RegionsModal from "../components/UI/RegionsModal";

import pin from "../images/pushpin.png";

import styles from "./RegionsPage.module.css";

const RegionsPage = () => {
  const [modalIsShown, setModalIsShown] = useState(false);
  const [matchaRegion, setMatchaRegion] = useState('');

  const hoverPinHandler = (region) => {
    setModalIsShown(true);
    setMatchaRegion(region)
  };

  const hideModalHandler = () => {
    setModalIsShown(false);
  }

  return (
    <div className={styles.container}>
      {modalIsShown && <RegionsModal region={matchaRegion} />}
      <div className={styles.container__header}>
        <RegionsText />
      </div>
      <div className={styles.container__body}>
        <div className={styles.container__img}>
          <RegionsMap />
        </div>
        <div className={styles.container__pins}>
          <img
            src={pin}
            className={`${styles.pin} ${styles.pin__1}`}
            onMouseEnter={() => hoverPinHandler('Uji')}
          ></img>
          <img
            src={pin}
            className={`${styles.pin} ${styles.pin__2}`}
            onMouseEnter={() => hoverPinHandler('Fukuoka')}
          ></img>
        </div>
      </div>
    </div>
  );
};

export default RegionsPage;
