  //     return updatedGoals;
  //   });
  // };

  // let content = (
  //   <p style={{ textAlign: "center" }}>No goals found. Maybe add one?</p>
  // );

  // if (courseGoals.length > 0) {
  //   content = (
  //     <CourseGoalList items={courseGoals} onDeleteItem={deleteItemHandler} />
  //   );
  // }

  const [courseGoals, setCourseGoals] = ([
    { text: 'Hit 150 Solidcore classes.', id: 'goal1'},
    { text: 'Play \'Brian\'s song well.', id: 'goal2'}
  ]);

  const addGoalHandler = (newGoalText) => {
    setCourseGoals((prevGoals) => {
      const updatedGoals = [...prevGoals];
      updatedGoals.unshift({ text: 'newGoalText', id: 'goal3'});
      return updatedGoals;
    });
  };

  const deleteGoalHandler = (goalId) => {
    setCourseGoals((prevGoals) => {
      const updatedGoals = prevGoals.filter((goal) => goal.id != goalId);
      return updatedGoals;
    });
  };

  let content = (
    <p style={{ textAlign: "center"}}>No goals found.</p>
  );

  if (courseGoals.length > 0) {
    content = (
      <CourseGoalList items={courseGoals} onDeleteItem={deleteGoalHandler}></CourseGoalList>
    );
  };

  return (
    <div>
      <section id="goal-form">
        <CourseInput onAddGoal={addGoalHandler} />
      </section>
      <section id="goals">{content}</section>
    </div>
  );
};

export default App;
