import React, { useState } from "react";

import matchaCup from "../../images/matchaCup.png";
import styles from "./MakeBox.module.css";

const steps = [
  {
    step: "1",
    description: "Sift 1-2 tsp matcha into a cup using a small sifter",
    img: { matchaCup },
  },
];

const MakeBox = () => {
  const [step, setStep] = useState({});

  return (
    <div className={styles.container}>
      <div className={styles.header}>
        <h3>
          <span>Step 1</span>
        </h3>
      </div>
      <div className={styles.body}>
        <img src={matchaCup}></img>
        <span>Sift the matcha to remove clumps.</span>
      </div>
    </div>
  );
};

export default MakeBox;
