import React, { Fragment, useState } from "react";

import Modal from "../UI/Modal";

import styles from "./RegionsModal.module.css";

const RegionsModal = (props) => {
  const [region, setRegion] = useState({});

  const getRegionData = async () => {
    const response = await fetch(
      "https://myikigai-99b57-default-rtdb.firebaseio.com/"
    );

    if (!response.ok) {
        console.log('error in fetching region');
    };

    const data = await response.json();
    console.log(data);

    const regionData = data.filter((region) => region.name === props.name);

    setRegion(regionData);
  };



  const content = <div>{region.name}</div>;

  return (
    <Fragment>
      <Modal onClose={props.onClose}>{content}</Modal>
    </Fragment>
  );
};

export default RegionsModal;
