import React, { useState } from "react";

import sifter from '../../images/sifter.png';
import styles from "./MakeBox.module.css";

const steps = [
  {
    step: "1",
    description: "Sift 1-2 tsp matcha into a cup using a small sifter.",
    img: { sifter },
  },
  {
    step: "2",
    description: "Add 2oz hot water. For best results use water just under a boil.",
    img: { matchaCup },
  },
  {
    step: "3",
    description: "Whisk vigorously in a zig zag motion until the tea is frothy.",
    img: { matchaCup },
  },
  {
    step: "4",
    description: "Enjoy your matcha tea straight from the bowl.",
    img: { matchaCup },
  },
];

const MakeBox = () => {
  const [currStep, setCurrStep] = useState(steps);

