import React, { useReducer, useState } from "react";
import Button from '../UI/Button';

import sifter from "../../images/sifter.png";

import styles from "./MakeBox.module.css";

const steps = [
  {
    step: "1",
    description: "Sift 1-2 tsp matcha into a cup using a small sifter.",
    img: '../../public/sifter.png'
  },
  {
    step: "2",
    description: "Add 2oz hot water. For best results use water just under a boil.",
    img: '../../public/sifter.png',
  },
  {
    step: "3",
    description: "Whisk vigorously in a zig zag motion until the tea is frothy.",
    img: '../../public/sifter.png',
  },
  {
    step: "4",
    description: "Enjoy your matcha tea straight from the bowl.",
    img: '../../public/sifter.png',
  },
];

const stepReducer = (state, action) => {
    if (action.type === 'INCREMENT_STEP') {
        state.currStep = state.currStep + 1;
    }
    if (action.type === 'DECREMENT_STEP') {
        state.currStep = state.currStep - 1;
    }

    return state;
}

const MakeBox = () => {
// const [stepState, dispatchStep] = useReducer(stepReducer, steps[0]);
const [stepState, dispatchStep] = useReducer(stepReducer, 0);

  const clickNextHandler = () => {
    dispatchStep({
        type: 'INCREMENT_STEP'
    });
  };

  const clickPrevioustHandler = () => {
    dispatchStep({
        type: 'INCREMENT_STEP'
    });
  };

  return (
    <div className={styles.container}>
      <div className={styles.header}>
        <h3>
          <span>{`Step ${currStep.step}`}</span>
        </h3>
      </div>
      <div className={styles.body}>
        <img src={sifter}></img>
        <span>{currStep.description}</span>
      </div>
      <div className={styles.buttons}>
        <Button type="submit" onClick={clickNextHandler}>Next</Button>
        <Button type="submit" onClick={clickPreviousHandler}>Back</Button>
      </div>
    </div>
  );
};

export default MakeBox;
