import React, { Fragment } from 'react';
import { ReactDOM } from 'react';

import styles from './RegionsModal.module.css';

const Backdrop = () => {
    return <div className={styles.backdrop}/>;
};

const ModalOverlay = (props) => {
    return (
      <div className={styles.modal}>
        <div className={styles.content}>{props.children}</div>
      </div>
    );
  };

  const portalElement = document.getElementById('overlays');

const RegionsModal = (props) => {
    return ( 
        <Fragment>
            {ReactDOM.createPortal(<Backdrop />, portalElement)}
            {ReactDOM.createPortal(<ModalOverlay>{props.children}</ModalOverlay>, portalElement)}
        </Fragment>
     );
}


const content = (
    <div className={styles.modal}>
      <div className={styles.modal__content}>
        <div className={styles.modal__header}>
          <h4 className={styles.modal__title}>Step 1</h4>
        </div>
        <div className={styles.modal__body}>First we sift our matcha</div>
        <div className={styles.modal__footer}>
          <button className="button">{props.onClose}</button>
        </div>
      </div>
    </div>
  );

export default RegionsModal;
