import React, { useState } from "react";

import styles from "./MakeBox.module.css";

import sifter from "../../images/sifter.png";

const steps = [
  {
    step: "1",
    description: "Sift 1-2 tsp matcha into a cup using a small sifter.",
    img: '../../public/sifter.png'
  },
  {
    step: "2",
    description: "Add 2oz hot water. For best results use water just under a boil.",
    img: './sifter.png',
  },
  {
    step: "3",
    description: "Whisk vigorously in a zig zag motion until the tea is frothy.",
    img: './sifter.png',
  },
  {
    step: "4",
    description: "Enjoy your matcha tea straight from the bowl.",
    img: './sifter.png',
  },
];

const MakeBox = () => {
  const [currStep, setCurrStep] = useState(steps[0]);

  return (
    <div className={styles.container}>
      <div className={styles.header}>
        <h3>
          <span>{`Step ${currStep.step}`}</span>
        </h3>
      </div>
      <div className={styles.body}>
        <img src={sifter}></img>
        <span>{currStep.description}</span>
      </div>
    </div>
  );
};

export default MakeBox;
