import React, { useState } from "react";

import Modal from "../components/UI/Modal";
import SubText from "../components/Homepage/SubText";

import matcha from '../images/matcha2.png';

import styles from "./MakeMatcha.module.css";

const MakeMatcha = () => {
  const [modalIsShown, setModalIsShown] = useState(false);

  const modalIsShownHandler = () => {
    setModalIsShown(true);
  };

  const hideModalHanlder = () => {
    setModalIsShown(false);
  };
  return (
    <div className={styles.container__sub2}>
      {modalIsShown && <Modal onClose={hideModalHanlder} />}
      <SubText />
      <div className={styles.subBackground}></div>
      <img
        src={matcha}
        className={styles.subImg}
        onClick={modalIsShownHandler}
      />
    </div>
  );
};

export default MakeMatcha;
