import React, { useReducer, useState } from "react";
import Button from "../UI/Button";

import sifter from "../../images/sifter.png";

import styles from "./MakeBox.module.css";

const steps = [
  {
    step: "1",
    description: "Sift 1-2 tsp matcha into a cup using a small sifter.",
    img: "../../public/sifter.png",
  },
  {
    step: "2",
    description:
      "Add 2oz hot water. For best results use water just under a boil.",
    img: "../../public/sifter.png",
  },
  {
    step: "3",
    description:
      "Whisk vigorously in a zig zag motion until the tea is frothy.",
    img: "../../public/sifter.png",
  },
  {
    step: "4",
    description: "Enjoy your matcha tea straight from the bowl.",
    img: "../../public/sifter.png",
  },
];

const stepReducer = (state, action) => {
  if (action.type === "INCREMENT_STEP") {
    return { step: state.step + action.payload };
  }
  if (action.type === "DECREMENT_STEP") {
    return { step: state.step - action.payload };
  }

  return { step: 0 };
};
const initialState = {
    stepIndex: 0,
    stepData: steps[stepIndex]
};

const MakeBox = () => {
  const [stepState, dispatchStep] = useReducer(stepReducer, initialState);
  const [currStep, setCurrStep] = useState(steps[stepState.step]);
  const [prevIsDisabled, setPrevIsDisabled] = useState(false);
  const [forwardIsDisabled, setForwardIsDisabled] = useState(false);

  const clickNextHandler = () => {
    dispatchStep({
      type: "INCREMENT_STEP",
      payload: 1,
    });

    if (stepState.step >= 3) {
      setForwardIsDisabled(true);
    } else {
      setCurrStep(steps[stepState]);
    }
    console.log(stepState.step);
    console.log(currStep);
    console.log(`Current step: ${currStep.step}`);
  };

  const clickPreviousHandler = () => {
    dispatchStep({
      type: "DECREMENT_STEP",
      payload: 1,
    });

    if (stepState.step <= 0) {
      setPrevIsDisabled(true);
    } else {
      setCurrStep(steps[stepState]);
    }
    console.log(stepState.step);
    console.log(currStep);
    console.log(`Current step: ${currStep.step}`);
  };

  return (
    <div className={styles.container}>
      <div className={styles.header}>
        <h3>
          <span>{`Step ${currStep.step}`}</span>
        </h3>
      </div>
      <div className={styles.body}>
        <img src={sifter}></img>
        <span>{currStep.description}</span>
      </div>
      <div className={styles.buttons}>
        <Button
          type="submit"
          className="forwardBtn"
          disabled={forwardIsDisabled}
          onClick={clickNextHandler}
        >
          {`>`}
        </Button>
        <Button
          type="submit"
          className="backBtn"
          disabled={prevIsDisabled}
          onClick={clickPreviousHandler}
        >
          {`<`}
        </Button>
      </div>
    </div>
  );
};

export default MakeBox;
