import React, { useState } from "react";

import HealthMainText from "../components/Health/HealthMainText";
import HealthModal from '../components/UI/HealthModal';

import drinkingMatchaImg from "../images/matchaGirl.png";
import bubble from '../images/bubble.png';
import star from '../'

import styles from "./HealthPage.module.css";

const HealthPage = () => {
    const [modalIsShown, setModalIsShown] = useState(false);
    const [benefit, setBenefit] = useState("");

    const showModalHandler = (healthBenefit) => {
        setModalIsShown(true);
        setBenefit(healthBenefit);
    };

    const hideModalHandler = () => {
      setModalIsShown(false);
    };

  return (
    <div className={styles.container}>
        {modalIsShown && <HealthModal benefit={benefit} onClose={hideModalHandler} />}
      <div className={styles.header}>
        <HealthMainText />
      </div>
      <div className={styles.body}>
        <div className={styles.image}>
          <img src={drinkingMatchaImg}></img>
        </div>
      </div>
      <div className={styles.container__bubbles}>
        <img
          src={bubble}
          className={`${styles.bubble} ${styles.bubble__1}`}
          onMouseEnter={() => showModalHandler("heart")}
        ></img>
        <img
          src={bubble}
          className={`${styles.bubble} ${styles.bubble__2}`}
          onMouseEnter={() => showModalHandler("energy")}
        ></img>
        <img
          src={bubble}
          className={`${styles.bubble} ${styles.bubble__3}`}
          onMouseEnter={() => showModalHandler("liver")}
        ></img>
      </div>
    </div>
  );
};

export default HealthPage;
